exports.ExecutionParameters = ExecutionParameters;

function ExecutionParameters(message) {
    if (!message) {
        throw new Error('Message is required');
    }

    this.message = message;
    this.configuration = {};
    this.snapshot = {};
}

ExecutionParameters.prototype.configuration = function snapshot(configuration) {
    if (!this.configuration) {
        throw new Error('Configuration is required');
    }
    this.configuration = configuration;

    return this;
};

ExecutionParameters.prototype.snapshot = function snapshot(snapshot) {
    if (!this.snapshot) {
        throw new Error('Snapshot is required');
    }

    this.snapshot = snapshot;

    return this;
};

